mapper.select:
SELECT a.ATCH_FILE_ID, b.FILE_CN, b.FILE_SN, b.FILE_STRE_COURS, b.STRE_FILE_NM, b.FILE_EXTSN, b.ORIGNL_FILE_NM, b.FILE_SIZE, a.CREAT_DT FROM ATCH_FILE a, ATCH_FILE_DETAIL b WHERE a.ATCH_FILE_ID = b.ATCH_FILE_ID
AND a.ATCH_FILE_ID = #{atchFileId}
AND USE_AT = 'Y' ORDER BY FILE_SN

mapper.delete:
DELETE FROM ATCH_FILE_DETAIL
WHERE
	ATCH_FILE_ID = #{atchFileId}
AND	FILE_SN = #{fileSn}

mapper.select:
SELECT IFNULL(MAX(FILE_SN),0)+1 AS FILE_SN
FROM ATCH_FILE_DETAIL
WHERE ATCH_FILE_ID =  #{atchFileId}

mapper.update:
UPDATE ATCH_FILE
SET USE_AT = 'N'
WHERE ATCH_FILE_ID = #{atchFileId}

mapper.select:
SELECT a.ATCH_FILE_ID, b.FILE_CN, b.FILE_SN, b.FILE_STRE_COURS, b.STRE_FILE_NM, b.FILE_EXTSN, b.ORIGNL_FILE_NM, b.FILE_SIZE, a.CREAT_DT FROM ATCH_FILE a, ATCH_FILE_DETAIL b WHERE a.ATCH_FILE_ID = b.ATCH_FILE_ID
AND a.ATCH_FILE_ID = #{atchFileId}
AND UPPER(FILE_EXTSN) IN ('GIF','JPG','BMP','PNG') AND USE_AT = 'Y' ORDER BY FILE_SN
